/* Initial goals */
!start.

/* Plans */
+!start: aliceEmailAndPass(AliceEMail,AlicePass) & emailProviderSettings(Receiving,Sending) <- 
    .print("Configuring my Email!");
    .mailer.credentials(AliceEMail,AlicePass);
    .mailer.eMailService(Receiving,Sending);

    .print("Requesting an email from bob");
    ?bobEmailAndPass(BobEMail,BobPass);
    .send(bob,achieve,configEmail(BobEMail,BobPass,Receiving,Sending));
    .send(bob,achieve,sendAnTestEMailTo(AliceEMail));
.

-!start <- .print("Something is wrong! Consult tutorial at: https://github.com/chon-group/Mailer/wiki").

+ping(Nr)[source(EmailAddress)]:Nr > 1 <- .print("The e-mail Middleware is working!"); .stopMAS.

+ping(Nr)[source(EmailAddress)]<-
    .print("Received ping=",Nr," from: ",EmailAddress);
    .mailer.sendEMail(EmailAddress,tell,ping(Nr+1))
.

-ping(Nr)[source(EmailAddress)] <- .stopMAS.

// Agent bob in project testingMailerArch

/* Plans */
+!sendAnTestEMailTo(Dest) <-
    .print("Sending e-mail to:",Dest);
    .mailer.sendEMail(Dest,tell,ping(0));
.

-!sendAnTestEMailTo(Dest) <- .stopMAS.

+!configEmail(Email,Pass,ReceivingConf,SendingConf) <-
    .print("Configuring my Email!");
    .mailer.credentials(Email,Pass);
    .mailer.eMailService(ReceivingConf,SendingConf);
.

+ping(Nr)[source(EmailAddress)]<-
    .print("Received ping=",Nr," from: ",EmailAddress);
    .mailer.sendEMail(EmailAddress,tell,ping(Nr+1))
.

-ping(Nr)[source(EmailAddress)] <- .stopMAS.
