/* To generate a random UUID use https://www.uuidgenerator.net/ */

senderUUID("80d9c5b3-5327-4836-b722-7481061affef").
receiverUUID("af467a22-eafc-4e87-9f57-882740ab0710").

/* We provide a Public IoT Gateway, more info in https://doi.org/10.5753/wei.2023.229753 */

gateway("skynet.chon.group",5500).  

/* Initial goals */

!start.

/* Plans */		

+!start: gateway(Server,Port) & senderUUID(ID) <-
	.print("Trying to connect in ",Server,":",Port);
	.connectCN(Server,Port,ID);
	.wait(5000);
	!testComm.

+!testComm: receiverUUID(Receiver) & not communication(ok)<-
	.print("I am calling the Receiver.");
	.sendOut(Receiver, tell, communication(trying)); 
	.wait(5000);
	!testComm.

+communication(ok)[source(X)]<-
	.print("IoT Gateway is working!"); 
	.stopMAS.

/* Initial goals */
!start.

/* Plans */
+!start <-
	.print("Asking for IoT Gateway");
	.send(sender,askOne,gateway(Server,Port));
	.send(sender,askOne,receiverUUID(ID));
	.wait(2000);
	!connect.

+!connect: gateway(Server,Port) & receiverUUID(ID)  <- 
	.print("Trying to connect in ",Server,":",Port);
	.connectCN(Server,Port,ID).

+communication(trying)[source(X)] <-
	.print("Answering call from ",X);
	.sendOut(X,tell,communication(ok)).
